use strict;
use warnings;
my $input = "



";

my @keypad = (
  ['-','-',1,'-','-'],
  ['-',2,3,4,'-'],
  [5,6,7,8,9],
  ['-','A','B','C','-'],
  ['-','-','D','-','-'],
);
my $x = 2;
my $y = 0;
my $code = '';
foreach my $line (split /\n/, $input) {
  $x = 2;
  $y = 0;
  foreach my $dir (split //, $line) {
    if($dir eq 'D'){
      if($x != 4){
        if($keypad[$x+1][$y]ne'-'){
          $x+=1;
        }
      }
    }elsif($dir eq 'U'){
      if($x != 0){
        if($keypad[$x-1][$y]ne'-'){
          $x-=1;
        }
      }
    }elsif($dir eq 'R'){
      if($y != 4){
        if($keypad[$x][$y+1]ne'-'){
          $y+=1;
        }
      }
    }elsif($dir eq 'L'){
      if($y != 0){
        if($keypad[$x][$y-1]ne'-'){
          $y-=1;
        }
      }
    }
  }

  $code = $code.$keypad[$x][$y];
  print "END OF LINE ";
  print "$code - $x,$y\n";
}

print $code;
